import { Struct } from 'superstruct';
/**
 * Assert a value against a Superstruct struct.
 *
 * @param value - The value to validate.
 * @param struct - The struct to validate against.
 * @param errorPrefix - A prefix to add to the error message. Defaults to
 * "Assertion failed".
 * @throws If the value is not valid.
 */
export declare function assertStruct<T, S>(value: unknown, struct: Struct<T, S>, errorPrefix?: string): asserts value is T;
